import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './css/Home.css';
import './App.js'
import './result.js'
function Home() {

    const [peopleCount, setPeopleCount] = useState(1);

    const [takeUrl, setTakeUrl] = useState([<><input type="text" name="1" id='1'></input><br/></>]);

    const [html, setHtml] = useState('HTML');

    return <>

    <div class='App'>

        <h1>Bob 먹자 Go</h1>
   <table className="table1">
                        <thead>
                            <tr>
                                <td className="th1">에브리타임 시간표 링크를 입력하세요</td>

                            </tr>
                        </thead>
                        <tbody>
                        <td className="td1">
                {takeUrl}
                </td> 


OM2 

                        </tbody>
                    </table>


        <input className= "button" type="button" value='+' onClick={()=> {

            var curr_peopleCount = peopleCount;
            curr_peopleCount++;
            setPeopleCount(curr_peopleCount);

            var curr_url = takeUrl;
            curr_url.push(
                <><input type="text" name={String(curr_peopleCount)} id={String(curr_peopleCount)}></input><br/></>);
            setTakeUrl(curr_url);
        }}></input>
        {/* <h1>{peopleCount}</h1> */}

            <input className= "button2" type='button' value={'결과보기'} onClick={(event) => {

                var urls = [];
                for(var i = 1; i <= peopleCount; i++) {
                    urls.push(document.getElementById(String(i)).value);
                }
                console.log('확인'+urls);
                axios.post('http://localhost:8080/processing_timetable', { urls: urls}).then((response) => {
                    // if (response.data.result !== {}) {
                    //     navigator('/main');
                    // }
                    // else {
                    //     alert('Fail!');
                    // }

                    setHtml(response.data.result);
                })

                navigator('/result');
            }}></input>

    </div>
    <div>
        <p>{html}</p>
    </div>
    </>
}
export default Home;
