import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './css/Home.css';
import './App.js'
import './result.js'
function Home() {

    const [peopleCount, setPeopleCount] = useState(1);

    const [takeUrl, setTakeUrl] = useState([<><input type="text" placeholder="TIMETABLE LINK" name="1" id='1'  class="u-input u-input-rectangle u-radius-12 u-white u-input-1" required=""></input><br/></>]);

    const [html, setHtml] = useState('HTML');

    return <>

    <div class='App'>





    <div class="u-clearfix u-valign-top u-section-1" id="carousel_1834">  
    <div class="u-container-style u-expanded-width u-group u-palette-5-light-2 u-shape-rectangle u-group-1">
    <div class="u-container-layout u-container-layout-1">
    <h1 class="u-align-center u-custom-font u-font-merriweather u-text u-text-default u-text-1">BOB-GO</h1>
    <h4 class="tex123">에브리타임 시간표 링크를 입력해 시간표를 ​비교해보세요 !</h4>

    <div>
        {takeUrl}
     </div>

    <div class="u-align-center u-form-group u-form-submit">
    <input type='button' className='button123' value={'submit'} onClick={(event) => {
    var urls = [];
    for(var i = 1; i <= peopleCount; i++) {
        urls.push(document.getElementById(String(i)).value);
    }
    console.log(urls);
    axios.post('http://localhost:8080/processing_timetable', { urls: urls}).then((response) => {
        // if (response.data.result !== {}) {
        //     navigator('/main');
        // }
        // else {
        //     alert('Fail!');
        // }

        setHtml(response.data.result);
    })
    }}>
    </input>
    <input type="submit" value="submit" class="u-form-control-hidden"/>
              </div>
 <input type="button" className= "button1234" value='인원추가' onClick={()=> {
            var curr_peopleCount = peopleCount;
            curr_peopleCount++;
            setPeopleCount(curr_peopleCount);

            var curr_url = takeUrl;
            curr_url.push(
                <><input input type="text" placeholder="TIMETABLE LINK" name={String(curr_peopleCount)} id={String(curr_peopleCount)} class="u-input u-input-rectangle u-radius-12 u-white u-input-1" required=""></input><br/></>);
            setTakeUrl(curr_url);
        }}></input>
       {/* <h1>{peopleCount}</h1> */}


    </div>
    </div>
    </div>
    </div>  
    <footer class="u-align-center u-clearfix u-footer u-grey-80 u-footer" id="sec-28c0"><div class="u-clearfix u-sheet u-sheet-1">
    <p class="u-small-text u-text u-text-variant u-text-1">Sample text. Click to select the Text Element.</p>
  </div></footer>
<section class="u-backlink u-clearfix u-grey-80">
  <a class="u-link" href="https://nicepage.com/website-templates" target="_blank">
    <span>Website Templates</span>
  </a>
  <p class="u-text">
    <span>created with yebin, minsoo</span>
  </p>
  <a class="u-link" href="" target="_blank">
    <span>Website Builder Software</span>
  </a>. 
</section>
    </>
}
export default Home;
