import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './css/Home.css';
import './App.js'
import './result.js'

async function validation(url_list) {
    var valid = 0;
    for(var i=0; i < url_list.length; i++) {
        if(url.length == 0) {
            valid = 0;
        }
        if(i==url_list.length-1) {
            return valid;
        }
    }
}

function Home() {

    let navigator = useNavigate();

    const [peopleCount, setPeopleCount] = useState(1);
    const [takeUrl, setTakeUrl] = useState([<><input type="text" placeholder="에브리타임 시간표 공유 링크를 입력해주세요" name="1" id='1' class="u-input u-input-rectangle u-radius-12 .u-grey-light-1 u-input-1" required=""></input><br /></>]);

    return <>

        <div class='App'>
            <div class="u-clearfix u-valign-top u-section-1" id="carousel_1834">
                <div class="u-container-style u-expanded-width u-group u-palette-5-light-2 u-shape-rectangle u-group-1">
                    <div class="u-container-layout u-container-layout-1">
                        <h1 class="u-align-center u-custom-font u-font-merriweather u-text u-text-default u-text-1">BOB-GO</h1>
                        <h4 class="tex123">에브리타임 시간표 링크를 입력해 시간표를 ​비교해보세요 !</h4>

                        <div>
                            {takeUrl}
                        </div>

                        <div class="u-align-center u-form-group u-form-submit">
                            <input type='button' className='button123' value={'submit'} onClick={async (event) => {
                                var urls = [];
                                for (var i = 1; i <= peopleCount; i++) {
                                    urls.push(document.getElementById(String(i)).value);
                                }
                                if (urls.length == 0) {
                                    alert("URL을 입력해주세요 :)");
                                }
                                else {
                                    var valid = await validation(urls);
                                    if(valid == 1) {
                                        axios.post('http://localhost:8080/processing_timetable', { urls: urls }).then((response) => {
                                            if (response.data.result !== {}) {
                                                console.log(response.data.result);
                                                navigator('/result', {
                                                    state: {
                                                        result: response.data.result,
                                                    }
                                                });
                                            }
                                            else {
                                                alert('Fail!');
                                            }
                                        })
                                    }
                                    else {
                                        alert('내용을 입력해주세요')
                                    }
                                }
                            }}>
                            </input>
                            <input type="submit" value="submit" class="u-form-control-hidden" />
                            <input style={{ display: 'inline', alignItems: 'center' }} type="button" className="button1234" value='인원추가' onClick={() => {
                                var curr_peopleCount = peopleCount;
                                curr_peopleCount++;
                                setPeopleCount(curr_peopleCount);
                                var curr_url = takeUrl;
                                curr_url.push(
                                    <><input input type="text" placeholder="에브리타임 시간표 공유 링크를 입력해주세요" name={String(curr_peopleCount)} id={String(curr_peopleCount)} class="u-input u-input-rectangle u-radius-12 .u-grey-light-1 u-input-1" required=""></input><br /></>);
                                setTakeUrl(curr_url);
                            }}>
                            </input>
                            <input style={{ display: 'inline', alignItems: 'center' }} type="button" className="button1234" value='인원삭제' onClick={() => {
                                var curr_peopleCount = peopleCount;
                                curr_peopleCount--;
                                setPeopleCount(curr_peopleCount);
                                var curr_url = takeUrl;
                                curr_url.pop();
                            }}>
                            </input>
                        </div>

                        {/* <h1>{peopleCount}</h1> */}
                    </div>
                </div>
            </div>
        </div>
        <footer class="u-align-center u-clearfix u-footer u-grey-80 u-footer" id="sec-28c0"><div class="u-clearfix u-sheet u-sheet-1">
            <p class="u-small-text u-text u-text-variant u-text-1">BobGo Service.</p>
            <p class="u-small-text u-text u-text-variant u-text-1">Created with YeBeen, MinSoo</p>

        </div>
        </footer>
        {/* <section class="u-backlink u-clearfix u-grey-80">
            <a class="u-link" href="https://nicepage.com/website-templates" target="_blank">
                <span>Website Templates</span>
            </a>
            <a class="u-link" href="" target="_blank">
                <span>Website Builder Software</span>
            </a>.
        </section> */}
    </>
}
export default Home;
